import axios from 'axios';
import { GET_ITEMS, ADD_ITEM, DELETE_ITEM } from './types.js';

export const getItems = () => dispatch => {
    dispatch(setItemsLoading());
    axios.get('/api/items').then(res => {
        dispatch({
            type: GET_ITEMS,
            payload: res.data
        })
    })
}

