import React, { Component } from 'react';
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody,
    Form,
    FormGroup,
    Label,
    Input
} from 'reactstrap';
import { connect } from 'react-redux';
import uuid from 'uuid';
import { addItem } from '../actions/itemActions';

class ItemModal extends Component{
    constructor(props){
        super(props);
        this.state = {
            modal: false,
            name: ''
        }
    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal
        })
    }

    onChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    onSubmit = e =>Â {
        e.preventDefault();
        const newItem = {
            id: uuid(),
            name: this.state.name
        }
        this.props.addItem(newItem);
    }

    render(){
        return(
            <div>
                <Button
                    color="dark"
                    style={{marginBottom:'2rem'}}
                    onClick={this.toggle}>
                    Add Item
                </Button>
                    <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
                    <ModalHeader toggle={this.toggle}>Add Item to Shopping List</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={this.onSubmit}>
                            <FormGroup>
                                <Label for="item">Item</Label>
                                <Input type="text" name="name" id="item" placeholder="What item do you need ?" onChange={this.onChange}/>
                                <Button color="dark" style={{marginTop:'2rem'}} block="true">Add Item</Button>
                            </FormGroup>
                        </Form>
                    </ModalBody>
                </Modal>
            </div>
        );
    }
}  

const mapStateToProps = state => ({
    item:state.item
})

export default connect(mapStateToProps, {addItem})(ItemModal);
